
import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { closeMenu } from "../utils/appSlice";
import { useSearchParams } from "react-router-dom";
import CommentsContainer from "./CommentsContainer";
import LiveChat from "./LiveChat";
import { Suggestions } from "./Suggestions";
import { options } from "../utils/constant";

const WatchPage = () => {
	const dispatch = useDispatch();
	
	
	const [searchParam] = useSearchParams();
	const [videosuggestion,setVideoSuggestion]=useState(null);
	const [videoDetail,setVideoDetail]=useState(null);
	// console.log(videoDetail);
	const getvideoDetail=async ()=>{
		const data=await fetch(`https://youtube-v31.p.rapidapi.com/videos?part=contentDetails%2Csnippet%2Cstatistics&id=${searchParam.get("v")}`,options)
		const json=await data.json();
		setVideoDetail(json);
	}
	const suggestVideo=async ()=>{
		const data=await fetch(`https://youtube-v31.p.rapidapi.com/search?relatedToVideoId=${searchParam.get("v")}&part=id%2Csnippet&type=video&maxResults=20`,options);
		const json=await data.json();
		setVideoSuggestion(json.items);
		// console.log(json.items);
	}
	// console.log(searchParam.get('v'));
	useEffect(() => {
		dispatch(closeMenu());
		window.scrollTo(0, 0);
		suggestVideo();
		getvideoDetail();
	}, [searchParam]);

	return (
		<>
			<div className=' flex flex-col pt-16 sm:pt-24  lg:pl-[5%]  w-full'>
				<div className="flex flex-col lg:flex-row lg:justify-between  w-full">
					<div className="flex-shrink-0 ">
						<iframe
							className='w-full max-w-[1000px] lg:min-w[600px]  aspect-video rounded-xl md:w[1000px] md:h-[500px]'
							
							src={
								"https://www.youtube.com/embed/" +
								searchParam.get("v") +
								"?si=vUI-Syiq6LaDc22o"
							}
							title='YouTube video player'
							allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share'
							allowFullScreen>

							</iframe>
							<p className=" pb-1 font-bold line-clamp-2 overflow-hidden text-ellipsis">{videoDetail?.items[0].snippet?.title}</p>
							<div className="flex gap-2">
							<img className='h-8 rounded-full ' src="data:image/png;base64," alt="user" />
                            <span className="font-semibold">{videoDetail?.items[0].snippet?.channelTitle}</span>
							<span className="flex h-5">
								<img className="w-5 " src="https://pic.onlinewebfonts.com/thumbnails/icons_265816.svg" alt="line" />
								<p className="">{videoDetail?.items[0].statistics.likeCount}</p>
							</span>
							</div>
					</div>
			      <LiveChat/>
				</div>
				<div className="flex">
				<CommentsContainer />
				{ videosuggestion && <Suggestions data={videosuggestion} />}
				</div>

			</div>
		</>
	);
};

export default WatchPage;



